INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('mesh','resource','Cryptosporidium','A genus of coccidian parasites of the family CRYPTOSPORIDIIDAE, found in the intestinal epithelium of many vertebrates including humans. (D003458)','FALSE','http://www.ncbi.nlm.nih.gov/mesh/68003458');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Giardia','An intestinal infection caused by a microscopic parasite that''s found worldwide, especially in areas with poor sanitation and unsafe water. Giardia infection is marked by abdominal cramps, bloating, nausea and bouts of watery diarrhea. The waterborne infection can be caused by parasites found in backcountry streams and lakes, as well as in municipal water supplies, swimming pools, whirlpool spas and wells, or transmitted through food and person-to-person contact.  The infection usually clears within a few weeks; prevention is the best defense. ','FALSE','http://www.mayoclinic.org/diseases- conditions/giardia-infection/basics/definition/con-20024686');
