INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('mesh','resource','Giardia','A genus of flagellate intestinal EUKARYOTES parasitic in various vertebrates, including humans. Characteristics include the presence of four pairs of flagella arising from a complicated system of axonemes and cysts that are ellipsoidal to ovoidal in shape. (D005872)','FALSE','http://www.ncbi.nlm.nih.gov/mesh/68005872');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Salmonella enterica','A widely distributed bacterial species that occurs in humans and in domestic and wild animals, especially rodents; it causes human gastroenteritis.','FALSE','http://medical-dictionary.thefreedictionary.com/Salmonella+enterica');
