INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('mesh','resource','Rotaviruses','A genus of REOVIRIDAE, causing acute gastroenteritis in BIRDS and MAMMALS, including humans. Transmission is horizontal and by environmental contamination. Seven species (Rotaviruses A thru G) are recognized. (D012401)','FALSE','http://www.ncbi.nlm.nih.gov/mesh/?term=Rotaviruses');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Noroviruses','Highly contagious virus spread through food or water contaminated by fecal matter, or close contact with an infected person.  Symptoms include diarrhea, abdominal pain, and vomiting lasting 1-3 days.  Infants, older adults, and people with underlying diseases are particularly vulnerable. ','FALSE','http://www.mayoclinic.org/diseases-conditions/norovirus/basics/definition/con-20029968');
