INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('mesh','resource','Noroviruses','A genus in the family CALICIVIRIDAE, associated with epidemic GASTROENTERITIS in humans. The type species, NORWALK VIRUS, contains multiple strains. (D029322)','FALSE','http://www.ncbi.nlm.nih.gov/mesh/?term=Noroviruses');
