INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Organohalogens','Any class of organic compounds that contain at least one halogen, bonded to carbon. Organohalogens are found in industrial chemicals (such as dyes, drugs, and synthetic polymers), and have also been identified as naturally occurring (produced by plants, fungi, bacteria, and marine organisms). ','FALSE','http://www.britannica.com/ science/organohalogen-compound');
