INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Organotins',' Any tin-based organic compound in wide use for marine anti-fouling paints, wood catalysts, plasticisers, slimicides, in industrial water systems and as fungicides. Human exposure results from contaminated seafood, agricultural products and drinking water. ','FALSE','http://medical-dictionary.thefreedictionary.com/Organotin+chemistry');
