INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Ciguatoxins','A seafood toxin that is acquired by eating fish that have consumed toxic single-celled marine organisms called dinoflagellates or fish that have consumed other fish that have become toxic. When someone eats these fish, they suffer seafood poisoning. Food poisoning from ciguatoxin is called ciguatera.','FALSE','http://www.medicinenet.com/script/main/art.asp?articlekey=19148');
