INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Saxitoxins','Saxitoxin (STX) is a potent neurotoxin, the best-known paralytic shellfish toxin (PST). Ingestion of saxitoxin (usually by consumption of shellfish contaminated by toxic algal blooms) is responsible for the human illness known as paralytic shellfish poisoning (PSP).','FALSE','https://en.wikipedia.org/wiki/Saxitoxin');
