INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Domoic Acids','Domoic acid (DA) is a kainic acid analog neurotoxin that causes amnesic shellfish poisoning (ASP). It is produced by algae and accumulates in shellfish, sardines, and anchovies.  Domoic acid is a neurotoxin that inhibits neurochemical processes, causing short-term memory loss, brain damage, and, in severe cases, death in humans.','FALSE','https://en.wikipedia.org/wiki/Domoic_acid');
