INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Okadaic Acids','Okadaic acid is a toxin produced by several species of dinoflagellates, and is known to accumulate in both marine sponges and shellfish.One of the primary causes of diarrhetic shellfish poisoning, okadaic acid is a potent inhibitor of specific protein phosphatases and is known to have a variety of negative effects on cells.','FALSE','https://en.wikipedia.org/wiki/Okadaic_acid');
