INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Drought','Drought is a deficiency in precipitation over an extended period, usually a season or more, resulting in a water shortage causing adverse impacts on vegetation, animals, and/or people.','FALSE','http://www.nws.noaa.gov/os/brochures/climate/DroughtPublic2.pdf');
