INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Runoff','That part of the precipitation, snow melt, or irrigation water that appears in uncontrolled surface streams, rivers, drains or sewers. Runoff may be classified according to speed of appearance after rainfall or melting snow as direct runoff or base runoff, and according to source as surface runoff, storm interflow, or groundwater runoff. ','FALSE','http://water.usgs.gov/edu/dictionary.html#R');
