INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Flooding','An overflow of water onto normally dry land. The inundation of a normally dry area causeby rising water in an existing waterway, such as a river, stream, or drainage ditch. Ponding of water at or near the point where the rain fell. Flooding is a longer term event than flash flooding: it may last days or weeks.','FALSE','http://www.srh.noaa.gov/mrx/hydro/ flooddef.php');
