INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('mesh','resource','E. coli','A species of gram-negative, facultatively anaerobic, rod-shaped bacteria (GRAM-NEGATIVE FACULTATIVELY ANAEROBIC RODS) commonly found in the lower part of the intestine of warm-blooded animals. It is usually nonpathogenic, but some strains are known to produce DIARRHEA and pyogenic infections. Pathogenic strains (virotypes) are classified by their specific pathogenic mechanisms such as toxins (ENTEROTOXIGENIC ESCHERICHIA COLI), etc (D004926)','FALSE','http://www.ncbi.nlm.nih.gov/mesh/68004926');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Vibrio vulnificus','Bacteria in the same family as those causing cholera and Vibrio parahaemolyticus living in warm seawater causing vomiting diarrhea, and abdominal pains when ingested. Persons can become infected when consuming raw seafood, particularly oysters','FALSE','http://www.cdc.gov/vibrio/vibriov.html');
