INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Salmonella bongori','Salmonella bongori is a pathogenic bacterium belonging to the genus Salmonella. It is a Gram negative rod-shaped bacterium (bacillus) which causes a gastrointestinal disease called salmonellosis, characterized by cramping and diarrhoea. ','FALSE','https://en.wikipedia.org/wiki/Salmonella_bongori');
