INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Ciguatera Fish Poisoning','An illness caused by eating fish containing toxins produced by a marine microalgae called Gambierdiscus toxicus. People may experience nausea, vomiting, and neurologic symptoms such as tingling fingers or toes. They also may find that cold things feel hot and hot things feel cold. Ciguatera has no cure. Symptoms usually go away in days or weeks but can last for years','FALSE','http://www.cdc.gov/nceh/ ciguatera/');
