INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES ('cdi','health','Salmonella','Bacteria causing people to develop diarrhea, fever, and abdominal cramps usually lasting 4-7 days. Salmonella is contracted from contaminated foods of animal origin such as raw and undercooked eggs, poultry, meat, and dairy products.','FALSE','http://www.cdc. gov/salmonella/general/prevention.html');
